//variable
var by_email = document.getElementById("email"),
    back_icon = document.querySelector(".fa-arrow-left");

// hide the default form
by_email.onclick = () => {
        //show the inscription form by email
        // hide the default form
        $("#default_form").hide("1000");
        $("#second_form").show("1000");
    }
    //show the default form
back_icon.onclick = () => {
    //show the inscription form by email
    // hide the default form
    $("#default_form").show("1000");
    $("#second_form").hide("1000");
}